<?php
error_reporting(E_ERROR);
include('connect_db.php');

if (isset($_GET['id']) && is_numeric($_GET['id']) && $_GET['id'] > 0) {
    // query db
    $id = $_GET['id'];
    $result = mysql_query("SELECT id,sharerlink FROM i_sharerlinks WHERE id='$id'") or die(mysql_error());
    $row = mysql_fetch_array($result);
    
    //check that the 'id' matches up with a row in the databse
    if ($row) {
        $url        = $row['sharerlink'];
        $removehttp = str_replace('http://', '', $url);
        $nakedip    = rtrim($removehttp, '/');
        if (strpos($nakedip, ':') !== false) {
            list($ip, $port) = explode(":", $nakedip);
        } else {
            $ip   = $nakedip;
            $port = 80;
        }
        
        
        if (fsockopen($ip, $port, $errno, $errstr, 5) !== false) {
            echo '<img src="data:png;base64," title="ONLINE">';
            mysql_query("UPDATE i_sharerlinks SET status='1' WHERE id='$id'") or die(mysql_error());
        } else {
            echo '<img src="data:png;base64," title="OFFLINE">';
            mysql_query("UPDATE i_sharerlinks SET status='0' WHERE id='$id'") or die(mysql_error());
        }
        
    }
} else {
    echo 'ERROR 404!';
}

?>